import { useEffect, useState } from 'react';
import './App.css';
import {
  BrowserRouter as Router,
  Routes,
  Route,
} from "react-router-dom";
import { Dashboard } from './pages/Dashboard';
import { Reports } from './pages/Reports';
import { ProfileDetails } from './pages/ProfileDetails';
import { LoadingSpinner } from './components/loadingSpinner';

function App() {
  const [isLoading,setIsLoading] = useState(true);
  // fetch api data and push them in the local storage
  // you can change the api url in the "src/api/config" file
  // the below function load once when the applicatin run
  useEffect(() => {
    //localStorage.clear();
    fetch(process.env.REACT_APP_BASE_URL + "/users/1" ,{method:'GET'})
    .then(res => res.json())
    .then(
      (result) => {
          localStorage.setItem('UserProfile', JSON.stringify(result));
          setIsLoading(false);
      },
      (error) => {
          console.log(error)
      }
    )
   }, [isLoading]);
  
   if(isLoading == false){
      return (
        //Router configuration
        //to add new router in the application
        <div className="App">
          <Router>
            <Routes>
              <Route path='/' element={<Dashboard />} />
              <Route path='/reports' element={<Reports />} />
              <Route path='/profile' element={<ProfileDetails />} />
            </Routes>
          </Router>    
        </div>
      );
   }
   else{
    return(
      <LoadingSpinner/>
     )
   }


}

export default App;
