import React, { useEffect, useState } from 'react';
import 'antd/dist/antd.css';
import './App.css';
import Navigation from './components/Navigation';
import { NavigationData } from './components/NavigationData';
import {API_URL} from './api/config'
import Profile from './components/Profile';
import { Layout } from 'antd';
const { Header, Footer, Sider, Content } = Layout;

function App() {
  const [profileCard, setProfileCard] = useState({name:'',phone:'',email:''});
  useEffect(() => {
    localStorage.clear();
    fetch(API_URL,{method:'GET'})
    .then(res => res.json())
    .then(
      (result) => {
          localStorage.setItem('UserProfile', JSON.stringify(result));
          //const items = JSON.parse(localStorage.getItem('UserProfile')!);
          setProfileCard(profileCard => ({
            ...profileCard,
            ...result
          }));
      },
      (error) => {
          console.log(error)
      }
    )
   }, []);

  return (
    <div className="App">
      <Profile name={profileCard.name} phone={profileCard.phone} email={profileCard.email} />
      <Navigation data={NavigationData} />
      <Layout>
        <Sider>Sider</Sider>
        <Layout>
          <Header>Header</Header>
          <Content>Content</Content>
          <Footer>Footer</Footer>
        </Layout>
      </Layout>
    </div>
  );
}

export default App;
