import React, { useEffect, useState } from "react";
import profile from '../images/profile.jpg';
export interface iProfileData {
    name:string,
    phone:string,
    email:string
}

const Profile = () => {
    const [profileCard, setProfileCard] = useState<iProfileData>({name:'',phone:'',email:''});
    useEffect(() => {
           const items = JSON.parse(localStorage.getItem('UserProfile')!);
           setProfileCard(profileCard => ({
            ...profileCard,
            ...items
          })); 
        })
          
    return (
        <div className="profileContainer">
            <div className="profileImage">
                <img src={profile} alt="profile"/>
            </div>
            <div>{profileCard.name}</div>
            <div>{profileCard.phone}</div>
            <div>{profileCard.email}</div>
        </div>
    )
}

export default Profile;
