import React, { useEffect, useState } from 'react';
import 'antd/dist/antd.css';
import '../App.css';
import Navigation from '../components/Navigation';
import { NavigationData } from '../components/NavigationData';
import {API_URL} from '../api/config'
import Profile from '../components/Profile';
import { Layout } from 'antd';
const { Header, Footer, Sider, Content } = Layout;

export const PageLayout = (props: any) => {
    const [profileCard, setProfileCard] = useState({name:'',phone:'',email:''});
    useEffect(() => {
           const items = JSON.parse(localStorage.getItem('UserProfile')!);
           setProfileCard(profileCard => ({
            ...profileCard,
            ...items
          }));       
    })
    return (
        <Layout>
            <Sider>
                <Profile name={profileCard.name} phone={profileCard.phone} email={profileCard.email} />
                <Navigation/>
            </Sider>
            <Layout>
                <Header>Header</Header>
                {props.children}
                <Footer>Footer</Footer>
            </Layout>
        </Layout>
    )
}
