  const [profileCard, setProfileCard] = useState({name:'',phone:'',email:''});
  useEffect(() => {
    localStorage.clear();
    fetch(API_URL,{method:'GET'})
    .then(res => res.json())
    .then(
      (result) => {
          localStorage.setItem('UserProfile', JSON.stringify(result));
          const items = JSON.parse(localStorage.getItem('UserProfile')!);
          console.log(items)
          setProfileCard(profileCard => ({
            ...profileCard,
            ...items
          }));
      },
      (error) => {
          console.log(error)
      }
    )
   }, []);

  return (
    <div className="App">
       <Profile name={profileCard.name} phone={profileCard.phone} email={profileCard.email}/>
       <Navigation data={NavigationData}/>
    </div>
  );
}

export default App;
